        <>
            <div className="mb-3">
                <label htmlFor="exampleFormControlTextarea1" className="form-label"><h2>Textarea</h2></label>
                <textarea className="form-control" id="textarea" rows="10" value={text} onChange={handle_change}></textarea>
            </div>
            <div>
            <button type="button" className="btn btn-primary m-2" onClick={handle_upper}>IntoUpperCase</button>
            <button type="button" className="btn btn-primary m-2" onClick={handle_lower}>IntoLowerCase</button>
            <button type="button" className="btn btn-primary m-2" onClick={handle_clear}>Clear</button>
            <button type="button" className="btn btn-primary m-2" onClick={handle_copy}>Copy</button>
            </div>
        </>
    )
}
